import React, { Component, useContext, useEffect } from "react";
import { PostContext } from "../App";
import "../css/LoginScreen.css";
function LoginScreen() {
  const { showNavBar, setShowOffNavBar } = useContext(PostContext);
  useEffect(() => {
    const a = () => {
      console.log(showNavBar);
      try {
        setShowOffNavBar(false);
      } catch (err) {}
    };
    a();
  }, []);
const submitHandler = async (e) =>{
    e.preventDefault()
}
  return (
    <center className="login_form">
      <div className="container_form">
        <div className="container_wrap">
          <form onSubmit={submitHandler}>
            <h1>Sign In</h1>
            <div className="mb-3 email">
              <h5>Email address</h5>

              <input
                type="email"
                className="form-control"
                placeholder="Enter email"
              />
            </div>
            <div className="mb-3 password">
              <h5>Password</h5>
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
              />
            </div>
            
            <div className="d-grid">
              <button type="submit" className="btn btn-primary login_btn">
                Submit
              </button>
            </div>
            <p className="forgot-password text-right dangky">
              Bạn chưa tạo tài khoản? <a href="#">Đăng ký</a>
            </p>
          </form>
        </div>
      </div>
    </center>
  );
}
export default LoginScreen;
