import {Card} from "react-bootstrap"
function Post(props) {
    const { post } = props;
  return (
    <Card>
        <Card.Img variant="top" src={post.image} />
        <Card.Body>
          <Card.Title>{post.title}</Card.Title>
          <Card.Text>
            {post.content.length > 100 ? post.content.slice(0,100).concat("..."): post.content}
          </Card.Text>
          <Card.Subtitle>{post.createdAt.getUTCFullYear() + "/" +
    ("0" + (post.createdAt.getUTCMonth()+1)).slice(-2) + "/" +
    ("0" + post.createdAt.getUTCDate()).slice(-2) + " " +
    ("0" + post.createdAt.getUTCHours()).slice(-2) + ":" +
    ("0" + post.createdAt.getUTCMinutes()).slice(-2) + ":" +
    ("0" + post.createdAt.getUTCSeconds()).slice(-2)}</Card.Subtitle>
        </Card.Body>
      </Card>
  );
}
export default Post;
