  return (
    <Card>
      <Card.Img variant="top" src={post.image} />
      <Card.Body>
        <Card.Title>{post.title}</Card.Title>
        <Card.Text>
          {post.content.length > 100
            ? post.content.slice(0, 100).concat("...")
            : post.content}
        </Card.Text>
        <Card.Subtitle>{createat}</Card.Subtitle>
      </Card.Body>
    </Card>
  );
}
export default Post;
