import {Card} from "react-bootstrap"
function Post(props) {
    const { post } = props;
  return (
    <Card>
        <Card.Img variant="top" src={post.image} />
        <Card.Body>
          <Card.Title>{post.title}</Card.Title>
          <Card.Text>
            {post.content.length > 10 ?post.content.slice(40).concat("..."):post.content}
          </Card.Text>
        </Card.Body>
      </Card>
  );
}
export default Post;
