import "./App.css";
import {
  Button,
  Navbar,
  NavDropdown,
  Nav,
  Container,
  Form,
  FormControl,
} from "react-bootstrap";
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";
import HomeScreen from "./screens/HomeScreen";
import PostScreen from "./screens/PostScreen";
import axios from "axios";
import { useEffect, useState } from "react";
import { apiUrl } from "./components/constant";
import SearchScreen from "./screens/SearchScreen";
import moment from "moment";
import React from "react";
import LoginScreen from "./screens/LoginScreen";
export const PostContext = React.createContext();
function App() {
  const [categories, setCategories] = useState([]);
  const [showNavBar, setShowNavBar] = useState(true);
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const { data } = await axios.get(`${apiUrl}/api/posts/categories`);
        setCategories(data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchCategories();
  }, []);
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");
  return (
    <PostContext.Provider value={{ posts, setPosts, search, setSearch,showNavBar,setShowNavBar }}>
      <BrowserRouter>
        <div className="App">
          <header className="App-header">
            <Navbar bg="white" expand="lg" className="ms-5">
              <Container>
                <Navbar.Brand href="/">
                  <img
                    src="https://s1.vnecdn.net/vnexpress/restruct/i/v607/v2_2019/pc/graphics/logo.svg"
                    alt="VnExpress - Bao tieng Viet nhieu nguoi xem nhat"
                  />
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                  <Nav className="me-auto">{moment().format("DD/MM/YYYY")}</Nav>
                </Navbar.Collapse>
              </Container>
            </Navbar>
            <div className="Nav-phu">
              <Button onClick={setShowNavBa} href="/login" variant="outline-success" className="ms-3">
                Đăng nhập
              </Button>
              <Form className="d-flex ms-3">
                <FormControl
                  type="search"
                  placeholder="Tìm kiếm"
                  className="me-2"
                  aria-label="Search"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
              </Form>
            </div>
          </header>
          {showNavBar && <div className="danh_muc">
            {categories.map((category) => (
              <Button
                href={`/search?category=${category}`}
                variant="outline-secondary"
                className="me-1 ms-1"
              >
                {category}
              </Button>
            ))}
          </div>}
          <main>
            <Container className="mt-3">
              <Routes>
                <Route path="/login" element={<LoginScreen />} />
                <Route path="/search" element={<SearchScreen />} />
                <Route path="/post/:slug" element={<PostScreen />} />
                <Route path="/" element={<HomeScreen />} />
              </Routes>
            </Container>
          </main>
        </div>
      </BrowserRouter>
    </PostContext.Provider>
  );
}

export default App;
