import "../css/PostScreen.css";
import { useNavigate,useParams } from "react-router-dom";
const reducer = (state, action) => {
  switch (action.type) {

    case "FETCH_SUCCESS":
      return { ...state, post: action.payload};
    case "FETCH_FAIL":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};
function PostScreen() {
  const navigate = useNavigate();
  const params = useParams();
  const { slug } = params;
  const [{  error, post }, dispatch] = useReducer(reducer, {
    post: [],
    error: "",
  });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`${apiUrl}/api/products/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: getError(err) });
      }
    };
    fetchData();
  }, [slug]);
  return (
    <div className="post_page">
      <div className="metadata">
        <a href="">
          <div className="category">Chinh tri</div>
        </a>
        <div className="createdat">Thứ ba, 24/5/2022, 18:01 (GMT+7)</div>
      </div>
      <div className="title">
        <h1>Làm rõ thiệt hại khi chậm hoàn thành đường Hồ Chí Minh</h1>
      </div>
      <div className="content">
        <p>

        Đại biểu Quốc hội Tạ Thị Yên đề nghị Chính phủ làm rõ việc chậm hoàn
        thành đường Hồ Chí Minh, làm giảm hiệu quả đầu tư và ảnh hưởng đến địa
        phương. Chiều 24/5, thảo luận tổ về dự án đường Hồ Chí Minh, Phó ban
        Công tác đại biểu Tạ Thị Yên nêu hàng loạt băn khoăn như thiệt hại này
        có định lượng được không, có lãng phí không? "Cơ quan chủ trì dự án nên
        trả lời trước Quốc hội về vấn đề này thì mới tính toán làm các đoạn còn
        lại", bà Yên nói. Qua 18 năm thực hiện Nghị quyết của Quốc hội, việc nối
        thông toàn tuyến đường Hồ Chí Minh năm 2020 như mục tiêu đề ra không
        thực hiện được. Đến nay, còn 6 đoạn dài 279 km chưa được triển khai. Bà
        Yên đề nghị Chính phủ làm rõ nguyên nhân giai đoạn 2021-2025 mới đề xuất
        làm 3 đoạn; còn 3 đoạn khác với 171 km chưa bố trí được vốn đầu tư. Theo
        Phó ban Công tác đại biểu, Chính phủ cần ưu tiên bố trí vốn đầu tư công
        trung hạn 2021-2025 để hoàn thành 171 km nói trên, kịp thông tuyến năm
        2025. "Cơ quan chủ trì dự án cần giải trình thêm với Quốc hội về kế
        hoạch làm các đoạn còn lại, không nên để dự án này cắt khúc", bà Yên nói.
        </p>
      </div>
    </div>
  );
}
export default PostScreen;
